@startuml
class Customer {
  customerId: String
  email: String
  password: String
  shippingAddress: String
  billingAddress: String
}

class Product {
  sku: String
  name: String
  description: String
  price: Double
  stockQuantity: Integer
}

class Category {
  categoryId: String
  name: String
  description: String
}

class Order {
  orderId: String
  orderDate: Date
  status: String
  totalAmount: Double
}

class OrderItem {
  quantity: Integer
  unitPrice: Double
}

class ShoppingCart {
  cartId: String
  createdDate: Date
}

class Payment {
  paymentId: String
  paymentMethod: String
  transactionId: String
  paymentStatus: String
  amount: Double
}

Customer "1" -- "0..*" Order
Customer "1" -- "0..1" ShoppingCart
Order "1" *-- "1..*" OrderItem
OrderItem "0..*" -- "1" Product
Product "0..*" -- "0..*" Category
Order "1" -- "0..1" Payment
ShoppingCart "1" -- "0..*" Product

@enduml

