  -wrappedContract: BasicContract
  +ContractDecorator(BasicContract contract)
  +getBalance(): double
  +setBalance(double balance): void
  +getCharges(): double
}

class DataOption {
  +DataOption(BasicContract contract)
  +getCharges(): double
}

class DoubleTransferRateOption {
  +DoubleTransferRateOption(BasicContract contract)
  +getCharges(): double
}

class MinutesOption {
  +MinutesOption(BasicContract contract)
  +getCharges(): double
}

class SMSOption {
  +SMSOption(BasicContract contract)
  +getCharges(): double
}

